async def echo(update: Update, context: ContextTypes.DEFAULT_TYPE):
    chat_id = update.message.chat.id
    user_text = update.message.text
    # ÐŸÐ¾Ð»ÑƒÑ‡Ð°ÐµÐ¼ Ñ‚ÐµÐºÑƒÑ‰ÐµÐµ ÑÐ¾ÑÑ‚Ð¾ÑÐ½Ð¸Ðµ Ð´Ð¸Ð°Ð»Ð¾Ð³Ð°, ÐµÑÐ»Ð¸ ÐµÐ³Ð¾ ÐµÑ‰Ñ‘ Ð½ÐµÑ‚ â€“ Ð±ÐµÑ€Ñ‘Ð¼ "start"
    current_state = USER_STATES.get(chat_id, "start")
    # Ð’Ñ‹Ð·Ñ‹Ð²Ð°ÐµÐ¼ Ñ„ÑƒÐ½ÐºÑ†Ð¸ÑŽ Ð»Ð¾Ð³Ð¸ÐºÐ¸ Ð´Ð¸Ð°Ð»Ð¾Ð³Ð°
    reply, new_state = get_response(user_text, current_state)
    USER_STATES[chat_id] = new_state
    await update.message.reply_text(reply)

async def main():
    app = Application.builder().token("YOUR_BOT_TOKEN_HERE").build()
    app.add_handler(CommandHandler("start", start))
    app.add_handler(MessageHandler(filters.TEXT & ~filters.COMMAND, echo))
    await app.initialize()
    await app.start()
    print("Ð‘Ð¾Ñ‚ Ð·Ð°Ð¿ÑƒÑ‰ÐµÐ½")
    await app.updater.start_polling()
    try:
        await asyncio.Event().wait()  # Ñ‡Ñ‚Ð¾Ð±Ñ‹ Ð¿Ñ€Ð¸Ð»Ð¾Ð¶ÐµÐ½Ð¸Ðµ Ð½Ðµ Ð·Ð°Ð²ÐµÑ€ÑˆÐ°Ð»Ð¾ÑÑŒ
    except KeyboardInterrupt:
        pass
    finally:
        await app.updater.stop()
        await app.stop()
        await app.shutdown()

